mapper.select:
with v1 as (
    SELECT *
    FROM t_domelist
    WHERE info = '11'

      and category = #{cd}

    order by rand() desc
) select *
  from v1 limit #{start} , #{end}

with v1 as (
    SELECT *
    FROM t_domelist
    WHERE info = '12'

        and category = #{cd}

    order by rand() desc
) select *
from v1 limit #{start} , #{end}

select name
from t_category_cd
where cd = #{cd}

select * from t_info_post
where category = #{category}
order by id desc

select *
from t_info_post
where category = #{category}
order by id desc

    LIMIT #{cnt}

select id,thumbnail, title from t_info_post
order by cnt desc
    limit #{cnt}

select title, writer, article, media, category, media_type, DATE_FORMAT(reg_dttm, '%Y-%m-%d') as 'reg_dttm'
from t_info_post
where id = #{id}

mapper.update:
update t_info_post
set cnt = cnt + 1
where id = #{id}

mapper.insert:
update t_info_post
set title=#{title}, writer=#{writer}, article=#{article}, media=#{media}, media_type=#{mediaType},
    category=#{category}

    ,thumbnail=#{thumbnail}

where id = #{id}

mapper.update:
update t_siteinfo
set cnt = cnt + 1
where name = #{name};
